sys_script.script:
(function executeRule(current, previous /*null when async*/) {


	var gr =  new GlideRecord('x_snc_complicatedw_weathersnapshot'); 
	gr.get(current.sys_id);

	var temp = null;
	var newTemp = null; 

	if (gr.unit == "C")
		{
			//send temp to F 
			temp = (gr.temperature * 1.8) + 32;
			newTemp = Math.round(temp);
			gr.temperature = newTemp;

		}
	else if (gr.unit == "F") {
			//send temp to C
			temp = (gr.temperature - 32) * .56;
			newTemp = Math.round(temp);
			gr.temperature = newTemp;

	}


	gs.info(gr.unit);
	gr.update();

 /*   
