        newTemp = (temp - 32) * .56;
        g_form.setValue('temperature', +(Math.round(newTemp + "e+2") + "e-2"));

    };

    if (prevUnit == "F") {
        toCelcius(temp);

    } else {
        toFahrenheit(temp);
    }
*/
})(current, previous);
